class EmptyState extends PureComponent {
  constructor(props) {
    super(props);

    this.renderToDo = this.renderToDo.bind(this);
    this.renderDoing = this.renderDoing.bind(this);
    this.renderDone = this.renderDone.bind(this);

    this.renderColumns = this.renderColumns.bind(this);
    this.renderArchive = this.renderArchive.bind(this);
  }

  renderToDo() {
    const { appMode } = this.props;
    if (appMode === 0) {
      return <ToDoLightSVG />;
    }
    if (appMode === 50) {
      return <ToDoDarkSVG />;
    }
    return <ToDoImageSVG />;
  }

  renderDoing() {
    const { appMode } = this.props;
    if (appMode === 0) {
      return <DoingLightSVG />;
    }
    if (appMode === 50) {
      return <DoingDarkSVG />;
    }
    return <DoingImageSVG />;
  }

  renderDone() {
    const { appMode } = this.props;
    if (appMode === 0) {
      return <DoneLightSVG />;
    }
    if (appMode === 50) {
      return <DoneDarkSVG />;
    }
    return <DoneImageSVG />;
  }

  renderDefault() {
    const { appMode } = this.props;
    if (appMode === 0) {
      return <DefaultSVG />;
    }
    return <DefaultDarkSVG />;
  }

  renderColumns() {
    const { stateType } = this.props;
    if (stateType === 'todo') {
      return this.renderToDo();
    }
    if (stateType === 'doing') {
      return this.renderDoing();
    }
    if (stateType === 'done') {
      return this.renderDone();
    }
    return this.renderDefault();
  }

  renderArchive() {
    const { appMode } = this.props;
    if (appMode === 0) {
      return <ArchiveLightMode />;
    }
    return <ArchiveDarkMode />;
  }

  render() {
    const { isArchive } = this.props;
    let state;
    if (isArchive) {
      state = this.renderArchive();
    } else {
      state = this.renderColumns();
    }
    if (!state) return null;
    return (
      <div className="column-empty-state">
        {state}
      </div>
    );
  }
}

EmptyState.defaultProps = {
  isArchive: false,
  stateType: '',
};

EmptyState.propTypes = {
  appMode: PropTypes.number.isRequired,
  isArchive: PropTypes.bool,
  stateType: PropTypes.string,
};

const mapStateToProps = ({ AppSettings }) => ({
  appMode: AppSettings.appMode,
});

export default connect(mapStateToProps)(EmptyState);
